// Importar complementos
import React from 'react';
import ReactDOM from 'react-dom';

// Classe
class Pagina extends React.Component{

    // Props e State
    constructor(props){

        super(props);

        this.state = {
            dados : []
        }

    }

    // Após criar o componente
    componentDidMount(){
        fetch("http://localhost:4000/", {method:"GET"})
        .then(resposta => resposta.json())
        .then((resultado) => {
            this.setState({
                dados : resultado
            })
        })
    }

    // Render
    render(){
        return(
            <table border="1">
                <thead>
                    <tr>
                        <th>Código</th>
                        <th>Nome</th>
                        <th>Idade</th>
                    </tr>
                </thead>

                <tbody>
                    {this.state.dados.map((obj) => (
                        <tr>
                            <td>{obj.codigo}</td>
                            <td>{obj.nome}</td>
                            <td>{obj.idade}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        )
    }

}

// Selecionando o id root
ReactDOM.render(<Pagina />, document.getElementById("root"));
