// Importar complementos
import React from 'react';
import ReactDOM from 'react-dom';

// Classe
class Pagina extends React.Component{

    // Props e State
    constructor(props){

        super(props);

        this.state = {
            dados : []
        }

    }

    // Após criar o componente selecionar os dados
    componentDidMount(){
        fetch("http://localhost:4000/", {method:"GET"})
        .then(resposta => resposta.json())
        .then((resultado) => {
            this.setState({
                dados : resultado
            })
        })
    }

    // Método para efetuar o cadastro
    cadastrar = () => {
        fetch("http://localhost:4000", {
            method: 'POST',
            body: JSON.stringify({'nome' : this.state.nome, 'idade' : this.state.idade}),
            headers: {"Content-Type": "application/json"}
        })
        .then(resposta => resposta.json())
        .then((resultado) => {

            // Copiar array
            var vetor = this.state.dados;

            // Adicionar dados vindos do back-end
            vetor.push(resultado);

            // Sobrepor dados do array
            this.setState({dados:vetor});
        });
    }

    excluir = (codigo) => {
      fetch("http://localhost:4000/"+codigo, {
        method: 'DELETE',
      })
      .then(res => res.json())
      .then(
        (result) => {
          alert(result.mensagem)
          window.location.reload(false);
        }
      )
    }


    aoTeclar = (e) => {
      let nam = e.target.name;
      let val = e.target.value;
      this.setState({[nam]: val});
    }

    // Render
    render(){
        return(
            <table border="1">
                <thead>
                    <tr>
                        <th>Código</th>
                        <th>Nome</th>
                        <th>Idade</th>
                    </tr>
                </thead>

                <tbody>
                    {this.state.dados.map((obj) => (
                        <tr>
                            <td>{obj.codigo}</td>
                            <td>{obj.nome}</td>
                            <td>{obj.idade}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        )
    }

}

// Selecionando o id root
ReactDOM.render(<Pagina />, document.getElementById("root"));
